% end

updateSflag = 1;
if updateSflag
    %https://www.phys.hawaii.edu/elog/mtc/338,  %255,65,13 -> 3,  101 -> 5,  0,102 -> 9,  64 -> 6
    SRCCH(SRCCH(:,1)==255 | SRCCH(:,1)==13 | SRCCH(:,1)==65, 1)=3;
    SRCCH(SRCCH(:,1)==101,                  1)=5;
    SRCCH(SRCCH(:,1)==102 | SRCCH(:,1)== 0, 1)=9;
    SRCCH(SRCCH(:,1)==64                  , 1)=6;
end

